import React from 'react'
import './App.css'
import fetch from 'node-fetch'

const today = new Date()
var ye = today.getFullYear()
var mo = ('0' + (today.getMonth() + 1)).slice(-2)
var da = ('0' + today.getDate()).slice(-2)
var day = ye + '/' + mo + '/' + da

export default class App extends React.Component {
  constructor (props) {
    super(props)
    this.state = {
      json: []
    }
  }

  onClick = () => {
    fetch('http://api.jugemkey.jp/api/horoscope/free/' + day)
      .then(response => {
        return response.json()
      })
      .then(json => {
        this.setState({
          json: json['horoscope'][day][0]
        })
      })
  }

  render () {
    return (
      <div className='app'>
        <h1>{day + 'の運勢'}</h1>
        <div class='btn'>
          <button onClick={this.onClick}>占う！</button>
        </div>
        <div>
          <ul class='luck'>
            <li>
              <span class='under'>金運</span>(5段階評価):{'  '}
              {JSON.stringify(this.state.json['money'])}
            </li>
            <li>
              <span class='under'>仕事運</span>(5段階評価):{'  '}
              {JSON.stringify(this.state.json['job'])}
            </li>
            <li>
              <span class='under'>恋愛運</span>(5段階評価):{'  '}
              {JSON.stringify(this.state.json['love'])}
            </li>
          </ul>
          <ul class='up'>
            <li>
              <span class='under2'>ラッキーアイテム</span>:{'  '}
              {JSON.stringify(this.state.json['item'])}
            </li>
            <li>
              <span class='under2'>ラッキーカラー</span>:{'  '}
              {JSON.stringify(this.state.json['color'])}
            </li>
          </ul>
          <p>{JSON.stringify(this.state.json['content'])}</p>
        </div>
      </div>
    )
  }
}

